Get-Mailbox -ResultSize unlimited | Select-Object name,UserPrincipalName,primarysmtpaddress,SamAccountName, DisplayName,Database,@{n="Size(MB)";e = {$MBXstat = Get-MailboxStatistics $_.name; $MBXstat.totalItemsize.value.toMB()}},@{n="Items"; e = {$MBXstat = Get-MailboxStatistics $_.name ; $MBXstat.itemcount}}, IsMailboxEnabled, ServerName,Office,WhenMailboxCreated,Alias,HiddenFromAddressListsEnabled,RecipientTypeDetails,OrganizationalUnit | Sort-Object 'Size(MB)' -descending | Export-Csv c:\temp\UserList.csv